pub enum Error<T: Transport, L: LoginCredentials> {

    /// Underlying transport failed to connect
    #[error("Underlying transport failed to connect: {0}")]
    ConnectError(Arc<T::ConnectError>),

    /// Underlying transport failed to connect in time
    #[error("Underlying transport failed to connect: Connect timed out")]
    ConnectTimeout,

    /// Error received from incoming stream of messages
    #[error("Error received from incoming stream of messages: {0}")]
    IncomingError(Arc<T::IncomingError>),

    /// Error received while trying to send message(s) out
    #[error("Error received while trying to send message(s) out: {0}")]
    OutgoingError(Arc<T::OutgoingError>),

    /// Incoming message was not valid IRC
    #[error("Incoming message was not valid IRC: {0}")]
    IRCParseError(IRCParseError),

    /// Failed to get login credentials to log in with
    #[error("Failed to get login credentials to log in with: {0}")]
    LoginError(Arc<L::Error>),

    /// Received RECONNECT command by IRC server
    #[error("Received RECONNECT command by IRC server")]
    ReconnectCmd,

    /// Did not receive a PONG back after sending PING
    #[error("Did not receive a PONG back after sending PING")]
    PingTimeout,

    /// Remote server unexpectedly closed connection
    #[error("Remote server unexpectedly closed connection")]
    RemoteUnexpectedlyClosedConnection,
}

pub enum ClearChatAction {

    /// A moderator cleared the entire chat.
    ChatCleared,

    /// A user was permanently banned.
    UserBanned {
        /// Login name of the user that was banned
        user_login: String,
        /// ID of the user that was banned
        user_id: String,
    },

    /// A user was temporarily banned (timed out).
    UserTimedOut {
        /// Login name of the user that was banned
        user_login: String,
        /// ID of the user that was banned
        user_id: String,
        /// Duration that the user was timed out for.
        timeout_length: Duration,
    },
}

pub enum HostTargetAction {

    /// Host mode was enabled (entered).
    HostModeOn {
        /// Login name of the channel that is now being hosted.
        hosted_channel_login: String,
        /// Optional: number of viewers watching the host. If missing this number is
        /// unknown at this moment.
        viewer_count: Option<u64>,
    },

    /// Host mode was disabled (exited).
    HostModeOff {
        /// Optional: number of viewers watching the host. If missing this number is
        /// unknown at this moment.
        viewer_count: Option<u64>,
    },
}

pub enum FollowersOnlyMode {

    /// Followers-only mode is/was disabled. All users, including user that are not followers,
    /// can send chat messages.
    Disabled,

    /// Followers-only mode is/was enabled. All users must have been following for at least this
    /// amount of time before being able to send chat messages.
    ///
    /// Note that this duration can be 0 to signal that all followers can chat. Otherwise,
    /// it will always a value that is a multiple of 1 minute. (1 minute is the highest resolution
    /// that can be specified)
    ///
    /// Moderator, VIPs or
    /// [verified bots](https://dev.twitch.tv/docs/irc/guide#known-and-verified-bots) bypass
    /// this setting and can send messages anyways.
    Enabled(Duration),
}

pub enum UserNoticeEvent {

    /// Emitted when a user subscribes or resubscribes to a channel.
    /// The user sending this `USERNOTICE` is the user subscribing/resubscribing.
    ///
    /// For brevity this event captures both `sub` and `resub` events because they both
    /// carry the exact same parameters. You can differentiate between the two events using
    /// `is_resub`, which is false for `sub` and true for `resub` events.
    SubOrResub {
        /// Indicates whether this is a first-time sub or a resub.
        is_resub: bool,
        /// Cumulative number of months the sending user has subscribed to this channel.
        cumulative_months: u64,
        /// Consecutive number of months the sending user has subscribed to this channel.
        streak_months: Option<u64>,
        /// `Prime`, `1000`, `2000` or `3000`, referring to Prime or tier 1, 2 or 3 subs respectively.
        sub_plan: String,
        /// A name the broadcaster configured for this sub plan, e.g. `The Ninjas` or
        /// `Channel subscription (nymn_hs)`
        sub_plan_name: String,
    },

    /// Incoming raid to a channel.
    /// The user sending this `USERNOTICE` message is the user raiding this channel.
    Raid {
        /// How many viewers participated in the raid and just raided this channel.
        viewer_count: u64,
        /// A link to the profile image of the raiding user. This is not officially documented
        /// Empirical evidence suggests this is always the 70x70 version of the full profile
        /// picture.
        ///
        /// E.g. `https://static-cdn.jtvnw.net/jtv_user_pictures/cae3ca63-510d-4715-b4ce-059dcf938978-profile_image-70x70.png`
        profile_image_url: String,
    },

    /// Indicates a gifted subscription.
    ///
    /// This event combines `subgift` and `anonsubgift`. In case of
    /// `anonsubgift` the sending user of the `USERNOTICE` carries no useful information,
    /// it can be e.g. the channel owner or a service user like `AnAnonymousGifter`. You should
    /// always check for `is_sender_anonymous` before using the sender of the `USERNOTICE`.
    SubGift {
        /// Indicates whether the user sending this `USERNOTICE` is a dummy or a real gifter.
        /// If this is `true` the gift comes from an anonymous user, and the user sending the
        /// `USERNOTICE` carries no useful information and should be ignored.
        is_sender_anonymous: bool,
        /// Cumulative number of months the recipient has subscribed to this channel.
        cumulative_months: u64,
        /// The user that received this gifted subscription or resubscription.
        recipient: TwitchUserBasics,
        /// `1000`, `2000` or `3000`, referring to tier 1, 2 or 3 subs respectively.
        sub_plan: String,
        /// A name the broadcaster configured for this sub plan, e.g. `The Ninjas` or
        /// `Channel subscription (nymn_hs)`
        sub_plan_name: String,
        /// number of months in a single multi-month gift.
        num_gifted_months: u64,
    },

    /// This event precedes a wave of `subgift`/`anonsubgift` messages.
    /// (`<User> is gifting <mass_gift_count> Tier 1 Subs to <Channel>'s community! They've gifted a total of <sender_total_gifts> in the channel!`)
    ///
    /// This event combines `submysterygift` and `anonsubmysterygift`. In case of
    /// `anonsubmysterygift` the sending user of the `USERNOTICE` carries no useful information,
    /// it can be e.g. the channel owner or a service user like `AnAnonymousGifter`. You should
    /// always check for `is_sender_anonymous` before using the sender of the `USERNOTICE`.
    SubMysteryGift {
        /// Indicates whether the user sending this `USERNOTICE` is a dummy or a real gifter.
        /// If this is `true` the gift comes from an anonymous user, and the user sending the
        /// `USERNOTICE` carries no useful information and should be ignored.
        /// Number of gifts the sender just gifted.
        mass_gift_count: u64,
        /// Total number of gifts the sender has gifted in this channel. This includes the
        /// number of gifts in this `submysterygift` or `anonsubmysterygift`.
        /// Note tha
        sender_total_gifts: u64,
        /// The type of sub plan the recipients were gifted.
        /// `1000`, `2000` or `3000`, referring to tier 1, 2 or 3 subs respectively.
        sub_plan: String,
    },

    /// This event precedes a wave of `subgift`/`anonsubgift` messages.
    /// (`An anonymous user is gifting <mass_gift_count> Tier 1 Subs to <Channel>'s community!`)
    ///
    /// This is a variant of `submysterygift` where the sending user is not known.
    /// Not that even though every `USERNOTICE` carries a sending user, the sending user of this
    /// type of `USERNOTICE` carries no useful information, it can be e.g. the channel owner
    /// or a service user like `AnAnonymousGifter`.
    ///
    /// Compared to `submysterygift` this does not provide `sender_total_gifts`.
    AnonSubMysteryGift {
        /// Number of gifts the sender just gifted.
        mass_gift_count: u64,
        /// The type of sub plan the recipients were gifted.
        /// `1000`, `2000` or `3000`, referring to tier 1, 2 or 3 subs respectively.
        sub_plan: String,
    },

    /// Occurs when a user continues their gifted subscription they got from a non-anonymous
    /// gifter.
    ///
    /// The sending user of this `USERNOTICE` is the user upgrading their sub.
    /// The user that gifted the original gift sub is specified by these params.
    GiftPaidUpgrade {
        /// User that originally gifted the sub to this user.
        /// This is the login name, see `TwitchUserBasics` for more info about the difference
        /// between id, login and name.
        gifter_login: String,
        /// User that originally gifted the sub to this user.
        /// This is the (display) name name, see `TwitchUserBasics` for more info about the
        /// difference between id, login and name.
        gifter_name: String,
        /// Present if this gift/upgrade is part of a Twitch gift sub promotion, e.g.
        /// Subtember or similar.
        promotion: Option<SubGiftPromo>,
    },

    /// Occurs when a user continues their gifted subscription they got from an anonymous
    /// gifter.
    ///
    /// The sending user of this `USERNOTICE` is the user upgrading their sub.
    AnonGiftPaidUpgrade {
        /// Present if this gift/upgrade is part of a Twitch gift sub promotion, e.g.
        /// Subtember or similar.
        promotion: Option<SubGiftPromo>,
    },

    /// A user is new in a channel and uses the rituals feature to send a message letting
    /// the chat know they are new.
    /// `<Sender> is new to <Channel>'s chat! Say hello!`
    Ritual {
        /// currently only valid value: `new_chatter`
        ritual_name: String,
    },

    /// When a user cheers and earns himself a new bits badge with that cheer
    /// (e.g. they just cheered more than/exactly 10000 bits in total,
    /// and just earned themselves the 10k bits badge)
    BitsBadgeTier {
        /// tier of bits badge the user just earned themselves, e.g. `10000` if they just
        /// earned the 10k bits badge.
        threshold: u64,
    },

    #[doc(hidden)]
    Unknown,
}

pub enum ServerMessageParseError {

    /// That command's data is not parsed by this implementation
    ///
    /// This type of error is only returned if you use `try_from` directly on a special
    /// server message implementation, instead of the general `ServerMessage::try_from`
    /// which covers all implementations and does not emit this type of error.
    #[error("Could not parse IRC message {} as ServerMessage: That command's data is not parsed by this implementation",
            . 0 . as_raw_irc())]
    MismatchedCommand(IRCMessage),

    /// No tag present under key `key`
    #[error("Could not parse IRC message {} as ServerMessage: No tag present under key `{1}`",
            . 0 . as_raw_irc())]
    MissingTag(IRCMessage, &'static str),

    /// No tag value present under key `key`
    #[error("Could not parse IRC message {} as ServerMessage: No tag value present under key `{1}`",
            . 0 . as_raw_irc())]
    MissingTagValue(IRCMessage, &'static str),

    /// Malformed tag value for tag `key`, value was `value`
    #[error("Could not parse IRC message {} as ServerMessage: Malformed tag value for tag `{1}`, value was `{2}`",
            . 0 . as_raw_irc())]
    MalformedTagValue(IRCMessage, &'static str, String),

    /// No parameter found at index `n`
    #[error("Could not parse IRC message {} as ServerMessage: No parameter found at index {1}",
            . 0 . as_raw_irc())]
    MissingParameter(IRCMessage, usize),

    /// Malformed channel parameter (`#` must be present + something after it)
    #[error("Could not parse IRC message {} as ServerMessage: Malformed channel parameter (# must be present + something after it)",
            . 0 . as_raw_irc())]
    MalformedChannel(IRCMessage),

    /// Malformed parameter at index `n`
    #[error("Could not parse IRC message {} as ServerMessage: Malformed parameter at index {1}",
            . 0 . as_raw_irc())]
    MalformedParameter(IRCMessage, usize),

    /// Missing prefix altogether
    #[error("Could not parse IRC message {} as ServerMessage: Missing prefix altogether",
            . 0 . as_raw_irc())]
    MissingPrefix(IRCMessage),

    /// No nickname found in prefix
    #[error("Could not parse IRC message {} as ServerMessage: No nickname found in prefix",
            . 0 . as_raw_irc())]
    MissingNickname(IRCMessage),
}

pub enum ServerMessage {

    /// `CLEARCHAT` message
    ClearChat(ClearChatMessage),

    /// `CLEARMSG` message
    ClearMsg(ClearMsgMessage),

    /// `GLOBALUSERSTATE` message
    GlobalUserState(GlobalUserStateMessage),

    /// `HOSTTARGET` message
    HostTarget(HostTargetMessage),

    /// `JOIN` message
    Join(JoinMessage),

    /// `NOTICE` message
    Notice(NoticeMessage),

    /// `PART` message
    Part(PartMessage),

    /// `PING` message
    Ping(PingMessage),

    /// `PONG` message
    Pong(PongMessage),

    /// `PRIVMSG` message
    Privmsg(PrivmsgMessage),

    /// `RECONNECT` message
    Reconnect(ReconnectMessage),

    /// `ROOMSTATE` message
    RoomState(RoomStateMessage),

    /// `USERNOTICE` message
    UserNotice(UserNoticeMessage),

    /// `USERSTATE` message
    UserState(UserStateMessage),

    /// `WHISPER` message
    Whisper(WhisperMessage),

    #[doc(hidden)]
    Generic(HiddenIRCMessage),
}

pub enum IRCPrefix {

    /// The prefix specifies only a sending server/hostname.
    ///
    /// Note that the spec also allows a very similar format where only a sending nickname is
    /// specified. However that type of format plays no role on Twitch, and is practically impossible
    /// to reliably tell apart from host-only prefix messages. For this reason, a prefix without
    /// a `@` character is always assumed to be purely a host-only prefix, and not a nickname-only prefix.
    HostOnly {
        /// `host` part of the prefix
        host: String,
    },

    /// The prefix variant specifies a nickname, and optionally also a username and optionally a
    /// hostname. See above for the RFC definition.
    Full {
        /// `nick` part of the prefix
        nick: String,
        /// `user` part of the prefix
        user: Option<String>,
        /// `host` part of the prefix
        host: Option<String>,
    },
}

pub enum IRCParseError {

    /// No space found after tags (no command/prefix)
    #[error("No space found after tags (no command/prefix)")]
    NoSpaceAfterTags,

    /// No tags after @ sign
    #[error("No tags after @ sign")]
    EmptyTagsDeclaration,

    /// No space found after prefix (no command)
    #[error("No space found after prefix (no command)")]
    NoSpaceAfterPrefix,

    /// No tags after : sign
    #[error("No tags after : sign")]
    EmptyPrefixDeclaration,

    /// Expected command to only consist of alphabetic or numeric characters
    #[error("Expected command to only consist of alphabetic or numeric characters")]
    MalformedCommand,

    /// Expected only single spaces between middle parameters
    #[error("Expected only single spaces between middle parameters")]
    TooManySpacesInMiddleParams,

    /// Newlines are not permitted in raw IRC messages
    #[error("Newlines are not permitted in raw IRC messages")]
    NewlinesInMessage,
}
